mapper.select:
		 select 

 		  from t_sms_log t
 		  where t.id = #{ id }

mapper.delete:
		 delete 
 		  from t_sms_log
 		  where id = #{ id }

mapper.select:
		 select 

 		  from t_sms_log t
 		  where 1=1 

 			 and t.id = #{id}


 			 and t.proId = #{proId}


 			 and t.verify_code = #{verifyCode}


 			 and t.mobile = #{mobile}


 			 and t.sms_type = #{smsType}


 			 and t.send_status = #{sendStatus}


 			 and t.use_status = #{useStatus}


 			 and t.sms_content = #{smsContent}


 			 and t.result = #{result}


 			 and t.send_time = #{sendTime}


 			 and t.update_time = #{updateTime}


 			 and t.request_ip = #{requestIp}


 			 and t.tenant_id = #{tenantId}

		 select 
 		  count(t.id)
 		  from t_sms_log t
 		  where 1=1 

 			 and t.id = #{id}


 			 and t.proId = #{proId}


 			 and t.verify_code = #{verifyCode}


 			 and t.mobile = #{mobile}


 			 and t.sms_type = #{smsType}


 			 and t.send_status = #{sendStatus}


 			 and t.use_status = #{useStatus}


 			 and t.sms_content = #{smsContent}


 			 and t.result = #{result}


 			 and t.send_time = #{sendTime}


 			 and t.update_time = #{updateTime}


 			 and t.request_ip = #{requestIp}


 			 and t.tenant_id = #{tenantId}
