mapper.select:
		 select 

 		  from t_union_account t
 		  where t.id = #{ id }

mapper.delete:
		 delete 
 		  from t_union_account
 		  where id = #{ id }

mapper.select:
		 select 

 		  from t_union_account t
 		  where 1=1 

 			 and t.id = #{id}


 			 and t.open_uuid = #{openUuid}


 			 and t.open_platform = #{openPlatform}


 			 and t.uuid = #{uuid}


 			 and t.create_time = #{createTime}


 			 and t.updated_time = #{updatedTime}

		 select 
 		  count(t.id)
 		  from t_union_account t
 		  where 1=1 

 			 and t.id = #{id}


 			 and t.open_uuid = #{openUuid}


 			 and t.open_platform = #{openPlatform}


 			 and t.uuid = #{uuid}


 			 and t.create_time = #{createTime}


 			 and t.updated_time = #{updatedTime}
