mapper.select:
		 select 

 		  from wk_sysconfig sysconfig
 		  where sysconfig.id = #{ id }

mapper.delete:
		 delete 
 		  from wk_sysconfig
 		  where id = #{ id }

mapper.select:
		 select 

 		  from wk_sysconfig sysconfig
 		  where 1=1 

 			 and sysconfig.id = #{id}


 			 and sysconfig.optionKey = #{optionKey}


 			 and sysconfig.optionValue = #{optionValue}


 			 and sysconfig.valueType = #{valueType}


 			 and sysconfig.remark = #{remark}


 			 and sysconfig.status = #{status}


 			 and sysconfig.system = #{system}


 			 and sysconfig.creator = #{creator}


 			 and sysconfig.createDate = #{createDate}


 			 and sysconfig.updator = #{updator}


 			 and sysconfig.updateDate = #{updateDate}


 			 and sysconfig.tenant_id = #{tenantId}

		 select 
 		  count(sysconfig.id)
 		  from wk_sysconfig sysconfig
 		  where 1=1 

 			 and sysconfig.id = #{id}


 			 and sysconfig.optionKey = #{optionKey}


 			 and sysconfig.optionValue = #{optionValue}


 			 and sysconfig.valueType = #{valueType}


 			 and sysconfig.remark = #{remark}


 			 and sysconfig.status = #{status}


 			 and sysconfig.system = #{system}


 			 and sysconfig.creator = #{creator}


 			 and sysconfig.createDate = #{createDate}


 			 and sysconfig.updator = #{updator}


 			 and sysconfig.updateDate = #{updateDate}


 			 and sysconfig.tenant_id = #{tenantId}
